mapper.insert:
insert into orders (
    order_uid
    ,member_uid
    ,menu_uid
    ,add_shot
    ,add_milk
    ,add_syrup
    ,select_bean
    ,count
    ,order_date
    ,order_state
)
values (
    nextval('order_seq')
    ,#{memberUid}
    ,#{menuUid}
    ,#{addShot}
    ,#{addMilk}
    ,#{addSyrup}
    ,#{selectBean}
    ,#{count}
    ,now()
    ,1
);
